    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String first=fname.getText();
        String last=lname.getText();
       String date=dob.getText();
       String address=city.getText();
       String id=email.getText();
       String user=uname.getText();
        String password=new String(pass.getPassword());
        String repassword=new String(repass.getPassword());
        int amount=0;
        if(agreecb.isSelected())
    {   if(password.equals(repassword))
        { match.setText("Password Matches");
        try{
Class.forName("java.sql.Driver");
Connection con =DriverManager.getConnection("jdbc:mysql://localhost:3306/project","chinmay","chinmay99");
Statement stmt=con.createStatement();
String query="insert into userinfo values('"+first+"','"+last+"','"+address+"','"+id+"','"+user+"','"+password+"',"+amount+");";
int rows=stmt.executeUpdate(query);
JOptionPane.showMessageDialog(null,"Now you are a part of G-Force Family!");
new Login().setVisible(true);
stmt.close();
con.close();// connection closed 
}
catch (Exception e)
{
JOptionPane.showMessageDialog(null,"Sorry UserName Already Exists");
} 
        }
        else
            match.setText("Password Did Not Match");
        }
        else
            JOptionPane.showMessageDialog(null,"Please Agree to our T&C"); 

    }//GEN-LAST:event_jButton1ActionPerformed

